from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
left = Motor(Port.B, Direction.CLOCKWISE)
right = Motor(Port.A, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
top = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left, right, 81, 99)


# The main program starts here.
drive_base.straight(200)
drive_base.turn(25)
drive_base.straight(360)
top.run_angle(500, -600)
drive_base.straight(-600)
